    public class MySqlCDateTimeUtil
    {
        // Accepts the regular date format of either 1/1/2017 or 01/01/2017
        // Use the overload for dd/mm/yyyy and pass "formatting"
        public static MySqlDateTime DateStrToMySQLDateTime(string date)
        {
            return DateStrToMySQLDateTime(date, "mm/dd/yyyy");
        }

        public static MySqlDateTime DateStrToMySQLDateTime(string date, string formatting)
        {
            char delimiter = '/';
            string[] arr = date.Split(delimiter);
            string month = arr[0].Length == 2 ? arr[0] : "0" + arr[0];
            string day = arr[1].Length == 2 ? arr[1] : "0" + arr[1];

            string mysqlDateStr = arr[2] + "-" + month + "-" + day;
            if (formatting == "dd/mm/yyyy")
            {
                mysqlDateStr = arr[2] + "-" + day + "-" + month;
            }

            MySqlDateTime result = new MySqlDateTime(mysqlDateStr);
            return result;
        }

        public static MySqlDateTime DateStrToMySQLDateTime(DateTime dateTime)
        {
            char delimiter = '/';
            string date = dateTime.ToShortDateString();
            string[] arr = date.Split(delimiter);
            string month = arr[0].Length == 2 ? arr[0] : "0" + arr[0];
            string day = arr[1].Length == 2 ? arr[1] : "0" + arr[1];

            string mysqlDateStr = arr[2] + "-" + month + "-" + day;

            MySqlDateTime result = new MySqlDateTime(mysqlDateStr);
            return result;
        }

        // The date param must be in the mysql format already (2017-01-03)
        public static MySqlDateTime StrToMySQLDateTime(string date)
        {
            MySqlDateTime result = new MySqlDateTime(date);
            return result;
        }

        // Converts MySQLformatted date (2017-01-03) to C# DateTime
        // Should ONLY be used if you are QUITE SURE the string is in the correct format and is a date!
        public static DateTime MySQLDateStringToDateTime(string date)
        {
            DateTime result = Convert.ToDateTime(date);
            return result;
        }

        public static DateTime? MySQLDateStringToNullableDateTime(string date)
        {
            DateTime? nullableDate = date != null ? Convert.ToDateTime(date) : (DateTime?)null;
            return nullableDate;
        }

        public static string FormatNullableDateTimeToShortDateString(string date)
        {
            DateTime? nullableDate = MySQLDateStringToNullableDateTime(date);
            string result = String.Empty;
            if (nullableDate != null)
            {
                DateTime date2 = (DateTime)nullableDate;
                result = date2.ToShortDateString();
            }

            return result;
        }

        public static string FormatNullableDateTimeToShortDateString(DateTime? date)
        {
            string result = String.Empty;
            if (date != null)
            {
                DateTime date2 = (DateTime)date;
                result = date2.ToShortDateString();
            }

            return result;
        }

        // function formats a date string generated from a datepicker (format 01/01/2015) to the correct value for mysql nullable date field
        public static string SetMYSQLQueryDateFromFormString(string formDateValue)
        {
            string result = null;
            if (!String.IsNullOrEmpty(formDateValue))
            {
                char delimiter = '/';
                string[] arr = formDateValue.Split(delimiter);
                string month = arr[0].Length == 2 ? arr[0] : "0" + arr[0];
                string day = arr[1].Length == 2 ? arr[1] : "0" + arr[1];

                string mysqlDate = arr[2] + "-" + month + "-" + day;
                result = mysqlDate;
            }

            return result;
        }

        // If you need MYSQL DateTimeStamp NOW, this function is the one to use.
        public static string MySQLDateTimeStampString()
        {
            DateTime now = DateTime.Now;
            string result = now.ToString("yyyy-MM-dd HH:mm:ss");

            return result;
        }

        public static string DateTimeToMySQLDateTimeStampString(DateTime dte)
        {
            return DateTimeToMySQLDateTimeStampString(dte, true);
        }

        public static string DateTimeToMySQLDateTimeStampString(DateTime dte, bool includeTime)
        {
            string result = "";
            if (includeTime)
            {
                result = dte.ToString("yyyy-MM-dd HH:mm:ss");
            }
            else
            {
                result = dte.ToString("yyyy-MM-dd");
            }

            return result;
        }

        public static string DateTimeToTimeStampString(DateTime dte)
        {
            string result = dte.ToString("MM/dd/yyyy HH:mm:ss");

            return result;
        }

        // Pass the time in this format: 
        public static MySqlDateTime Time12To24HourDateTime(string time)
        {
            DateTime dte = DateTime.Parse(time);
            dte.ToString("HH:mm");

            MySqlDateTime result = new MySqlDateTime(dte);
            return result;
        }


        public static string CurrentMonthLong()
        {
            DateTime now = DateTime.Now;
            int month = now.Month;

            Enumerations.Month enumDisplayName = (Enumerations.Month)month;
            string stringValue = enumDisplayName.ToString();

            return stringValue;
        }

        public static int CurrentMonthInt()
        {
            DateTime now = DateTime.Now;
            return now.Month;
        }

        // Get a MYSQL date range from date (If no date passed, then today's date will be used)
        // Date passed must be in valid format because it will be converted to DateTime (valid formats: 1/1/2017 or 01/01/2017)
        // Note: If you pass a date not on the first of the month, the start date will still return the first day of that month.
        public static string[] GetMySQLDateRange(string date)
        {
            DateTime dateTime = date == null ? DateTime.Now : Convert.ToDateTime(date);
            string dateTimeFirstDay = dateTime.Month.ToString() + "/1/" + dateTime.Year.ToString();
            string startDateMySQL = SetMYSQLQueryDateFromFormString(dateTimeFirstDay);

            int lastDay = date == null ? DateTime.Now.Day : DateTime.DaysInMonth(dateTime.Year, dateTime.Month);
            string endDate = dateTime.Month + "/" + lastDay + "/" + dateTime.Year;
            string endDateMySQL = SetMYSQLQueryDateFromFormString(endDate);

            string[] range = new string[] { startDateMySQL, endDateMySQL };
            return range;
        }

        // Overload, allowing you to pass a real DateTime instead
        public static string[] GetMySQLDateRange(DateTime dateTime)
        {
            string dateTimeFirstDay = dateTime.Month.ToString() + "/1/" + dateTime.Year.ToString();
            string startDateMySQL = SetMYSQLQueryDateFromFormString(dateTimeFirstDay);

            int lastDay = dateTime.Date == DateTime.Now.Date ? DateTime.Now.Day : DateTime.DaysInMonth(dateTime.Year, dateTime.Month);
            string endDate = dateTime.Month + "/" + lastDay + "/" + dateTime.Year;
            string endDateMySQL = SetMYSQLQueryDateFromFormString(endDate);

            string[] range = new string[] { startDateMySQL, endDateMySQL };
            return range;
        }

        public static string[] GetNormalDateRange()
        {
            DateTime dateTime = DateTime.Now;

            string month = dateTime.Month.ToString().Length == 2 ? dateTime.Month.ToString() : "0" + dateTime.Month.ToString();

            string dateTimeFirstDay = month + "/01/" + dateTime.Year.ToString();

            int lastDay = DateTime.Now.Day;   // Only allow up to today
            string endDate = month + "/" + lastDay + "/" + dateTime.Year;

            string[] range = new string[] { dateTimeFirstDay, endDate };
            return range;
        }

        // Get regular formatted daterange. If no date passed, it will use DateTime.Now
        // Format returned: 01/01/2017
        public static string[] GetNormalDateRange(DateTime? date)
        {
            DateTime dateTime = DateTime.Now;
            if (date.HasValue)
            {
                dateTime = (DateTime)date;
            }

            string month = dateTime.Month.ToString().Length == 2 ? dateTime.Month.ToString() : "0" + dateTime.Month.ToString();

            string dateTimeFirstDay = month + "/01/" + dateTime.Year.ToString();

            int lastDay = dateTime.Date == DateTime.Now.Date ? DateTime.Now.Day : DateTime.DaysInMonth(dateTime.Year, dateTime.Month);
            string endDate = month + "/" + lastDay + "/" + dateTime.Year;

            string[] range = new string[] { dateTimeFirstDay, endDate };
            return range;
        }

        public static string[] GetNormalDateRange(string dateStr)
        {
            DateTime dateTime = DateTime.Now;
            if (!String.IsNullOrEmpty(dateStr))
            {
                dateTime = Convert.ToDateTime(dateStr);
            }

            string month = dateTime.Month.ToString().Length == 2 ? dateTime.Month.ToString() : "0" + dateTime.Month.ToString();

            string dateTimeFirstDay = month + "/01/" + dateTime.Year.ToString();

            int lastDay = dateTime.Date == DateTime.Now.Date ? DateTime.Now.Day : DateTime.DaysInMonth(dateTime.Year, dateTime.Month);
            string endDate = month + "/" + lastDay + "/" + dateTime.Year;

            string[] range = new string[] { dateTimeFirstDay, endDate };
            return range;
        }

        // Pass a string formatted "yyyyMMddHHmmss" and get a DateTime
        public static DateTime GetDateTimeFromYYYYMMddHHmmss(string date)
        {
            DateTime dte = DateTime.ParseExact(date, "yyyyMMddHHmmss", null);
            return dte;
        }

        public static DateTime GetDateTimeWithTimeZoneOffset(DateTime dte, int hours)
        {
            double dbHours = Convert.ToDouble(hours);
            return dte.AddHours(dbHours);
        }

        public static List<TimezoneData> GetTimezoneData()
        {
            List<TimezoneData> data = new List<TimezoneData>();
            var hexVal = "";
            var json = GlobalHelper.HexToStr(hexVal);

            var jsonObj = JsonConvert.DeserializeObject<JToken>(json);
            var timezones = JsonConvert.DeserializeObject<JToken>(jsonObj["Timezones"].ToString());
            foreach (var item in timezones)
            {
                TimezoneData x = new TimezoneData();
                x.OffSet = Convert.ToInt16(item["offset"]);
                x.Text = item["text"].ToString();
                data.Add(x);
            }

            return data;
        }
    }
